package interview;

public class Primenumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number =17;
		int i=2;
	boolean	 prime=true;
		while(i<number) {
			if(number%i==0) {
				System.out.println("not prime number");
				prime=false;
			}
			if (prime==true) {
				System.out.println("prime number");
				
			}
					
					
				}
				
			}
		
	}


