package interview;

public class SwaptheIntegers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=20;
		System.out.println("before swap"+ "a =" +a + "b= "+b);
		int c=a+b;
		
		b=c-b;
		a=c-b;
		System.out.println("Afterswap swap"+ "a =" +a + "b= "+b);
		
		
	}

}
